/** @jsx jsx */
import { jsx } from 'theme-ui';
import Link from 'next/link';

export default () => (
  <div sx={{ height: `calc(100vh - 60px)` }}>
    <div
      sx={{
        variant: 'containers.page',
        display: 'flex',
        alignItems: 'center',
        height: '100%',
      }}
    >
      <h1 sx={{ fontSize: 6, my: 0 }}>
        This is a really dope note taking app.
      </h1>
      <a
        sx={{
          color: 'text',
          fontSize: 3,
          cursor: 'pointer',
        }}
        href={process.env.HELP_APP_URL}
      >
        Help
      </a>
    </div>
  </div>
);
